import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../Context/AuthContext";
import { getProductImageUrl, handleImageError } from "../utils/imageUtils";
import api from "../utils/api";
import "./ProductDetails.css";

const ProductDetails = () => {
  const { id } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isInWishlist, setIsInWishlist] = useState(false);
  const [wishlistLoading, setWishlistLoading] = useState(false);
  const [addingToCart, setAddingToCart] = useState(false);

  useEffect(() => {
    const fetchProductDetails = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`http://localhost:5000/products/${id}`);
        setProduct(response.data.product);

        if (user) {
          checkWishlistStatus(user._id, id);
        }
      } catch (error) {
        console.error("Error fetching product details:", error);
        setError("Failed to fetch product details. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchProductDetails();
  }, [id, user]);

  const checkWishlistStatus = async (userId, productId) => {
    try {
      const response = await api.get(`/wishlists/user/${userId}`);
      const inWishlist = response.data.items?.some(
        (item) => item.productId && item.productId._id === productId
      );
      setIsInWishlist(inWishlist);
    } catch (error) {
      console.error("Error checking wishlist status:", error);
      setIsInWishlist(false);
    }
  };

  const handleWishlistAction = async () => {
    if (!user) {
      navigate("/login");
      return;
    }

    try {
      setWishlistLoading(true);
      if (isInWishlist) {
        await api.delete(`/wishlists/remove/${user._id}/${id}`);
        setIsInWishlist(false);
      } else {
        await api.post("/wishlists/add", {
          userId: user._id,
          productId: id,
        });
        setIsInWishlist(true);
      }
    } catch (error) {
      console.error("Error updating wishlist:", error);
      alert(
        error.response?.data?.error ||
          "Failed to update wishlist. Please try again."
      );
    } finally {
      setWishlistLoading(false);
    }
  };

  const handleAddToCart = async () => {
    if (!user) {
      navigate("/login");
      return;
    }

    try {
      setAddingToCart(true);
      await axios.post("http://localhost:5000/cart", {
        userId: user._id,
        productId: id,
        quantity: quantity,
      });
      alert(`${quantity} ${product.name}(s) added to cart!`);
    } catch (error) {
      console.error("Error adding to cart:", error);
      alert(
        error.response?.data?.error ||
          "Failed to add item to cart. Please try again."
      );
    } finally {
      setAddingToCart(false);
    }
  };

  const handleBuyNow = async () => {
    if (!user) {
      navigate("/login");
      return;
    }

    try {
      setAddingToCart(true);
      await axios.post("http://localhost:5000/cart", {
        userId: user._id,
        productId: id,
        quantity: quantity,
      });
      navigate("/checkout");
    } catch (error) {
      console.error("Error adding to cart:", error);
      alert(
        error.response?.data?.error ||
          "Failed to proceed to checkout. Please try again."
      );
    } finally {
      setAddingToCart(false);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading product details...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <div className="error-icon">!</div>
        <p>{error}</p>
        <button onClick={() => window.location.reload()} className="retry-button">
          Retry
        </button>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="not-found-container">
        <h2>Product Not Found</h2>
        <p>
          The product you're looking for doesn't exist or may have been removed.
        </p>
        <button onClick={() => navigate("/products")} className="browse-button">
          Browse Products
        </button>
      </div>
    );
  }

  return (
    <div className="product-details-container">
      <div className="product-details-grid">
        <div className="product-image-container">
          <img
            src={getProductImageUrl(product.image)}
            alt={product.name}
            className="product-main-image"
            onError={handleImageError}
          />
        </div>

        <div className="product-info-container">
          <h1 className="product-title">{product.name}</h1>
          {product.brand && <p className="product-brand">By {product.brand}</p>}

          <div className="product-price-container">
            {product.originalPrice && (
              <span className="original-price">
                Rs. {product.originalPrice.toFixed(2)}
              </span>
            )}
            <span className="current-price">
              Rs. {product.price.toFixed(2)}
            </span>
            {product.originalPrice && (
              <span className="discount-badge">
                {Math.round((1 - product.price / product.originalPrice) * 100)}% OFF
              </span>
            )}
          </div>

          <div className="product-meta">
            <div className="meta-item">
              <span className="meta-label">Availability:</span>
              <span className={`meta-value ${product.availability ? 'in-stock' : 'out-of-stock'}`}>
                {product.availability ? 'In Stock' : 'Out of Stock'}
              </span>
            </div>
            <div className="meta-item">
              <span className="meta-label">Category:</span>
              <span className="meta-value">{product.category}</span>
            </div>
            {product.material && (
              <div className="meta-item">
                <span className="meta-label">Material:</span>
                <span className="meta-value">{product.material}</span>
              </div>
            )}
            {product.ratings && (
              <div className="meta-item">
                <span className="meta-label">Rating:</span>
                <div className="product-ratings">
                  {[...Array(5)].map((_, i) => (
                    <span
                      key={i}
                      className={`star ${i < Math.floor(product.ratings) ? 'filled' : ''}`}
                    >
                      ★
                    </span>
                  ))}
                  <span className="rating-count">({product.ratingCount || 0})</span>
                </div>
              </div>
            )}
          </div>

          {product.description && (
            <div className="product-description">
              <h3>Description</h3>
              <p>{product.description}</p>
            </div>
          )}

          {product.availability && (
            <div className="product-actions">
              <div className="quantity-selector">
                <label>Quantity:</label>
                <div className="quantity-controls">
                  <button
                    onClick={() => setQuantity(prev => Math.max(1, prev - 1))}
                    disabled={quantity <= 1}
                  >
                    -
                  </button>
                  <span>{quantity}</span>
                  <button
                    onClick={() => setQuantity(prev => Math.min(10, prev + 1))}
                    disabled={quantity >= 10}
                  >
                    +
                  </button>
                </div>
              </div>

              <div className="action-buttons">
                <button
                  className="add-to-cart-btn"
                  onClick={handleAddToCart}
                  disabled={addingToCart}
                >
                  {addingToCart ? 'Adding...' : 'Add to Cart'}
                </button>
                <button
                  className="buy-now-btn"
                  onClick={handleBuyNow}
                  disabled={addingToCart}
                >
                  {addingToCart ? 'Processing...' : 'Buy Now'}
                </button>
                <button
                  className={`wishlist-btn ${isInWishlist ? 'in-wishlist' : ''}`}
                  onClick={handleWishlistAction}
                  disabled={wishlistLoading}
                >
                  {wishlistLoading ? (
                    <span className="spinner"></span>
                  ) : (
                    <>
                      {isInWishlist ? '✓ In Wishlist' : '♡ Add to Wishlist'}
                    </>
                  )}
                </button>
              </div>
            </div>
          )}
        </div>
      </div>

      <button className="back-button" onClick={() => navigate(-1)}>
        ← Back to Products
      </button>
    </div>
  );
};

export default ProductDetails;
