import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import './AddProduct.css';

const AddProduct = () => {
  const navigate = useNavigate();
  const [productData, setProductData] = useState({
    name: "",
    category: "",
    price: "",
    material: "",
    stockQuantity: 0,
    availability: true,
    image: null
  });

  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProductData({ ...productData, [name]: value });
  };

  const handleFileChange = (e) => {
    setProductData({ ...productData, image: e.target.files[0] });
  };

  const validateForm = () => {
    const newErrors = {};

    if (!productData.name.trim()) {
      newErrors.name = "Product name is required";
    }

    if (!productData.category.trim()) {
      newErrors.category = "Category is required";
    }

    if (!productData.price) {
      newErrors.price = "Price is required";
    } else if (isNaN(productData.price) || productData.price <= 0) {
      newErrors.price = "Price must be a positive number";
    }

    if (!productData.material.trim()) {
      newErrors.material = "Material is required";
    }

    if (productData.stockQuantity < 0) {
      newErrors.stockQuantity = "Stock quantity cannot be negative";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;
    
    setIsSubmitting(true);
    
    try {
      const formData = new FormData();
      formData.append('name', productData.name);
      formData.append('category', productData.category);
      formData.append('price', productData.price);
      formData.append('material', productData.material);
      formData.append('stockQuantity', productData.stockQuantity);
      formData.append('availability', productData.availability);
      if (productData.image) {
        formData.append('image', productData.image);
      }

      const response = await axios.post("http://localhost:5000/products", formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      alert("Product added successfully!");
      navigate("/admin/products");
    } catch (error) {
      console.error("Error adding product:", error);
      alert("Failed to add product. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="add-product-container">
      <h2>Add Product</h2>
      <form className="add-product-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name:</label>
          <input 
            type="text" 
            name="name" 
            value={productData.name} 
            onChange={handleInputChange} 
          />
          {errors.name && <span className="error">{errors.name}</span>}
        </div>

        <div className="form-group">
          <label>Category:</label>
          <select 
            name="category" 
            value={productData.category} 
            onChange={handleInputChange}
          >
            <option value="">Select a category</option>
            <option value="living room">Living Room</option>
            <option value="kitchen">Kitchen</option>
            <option value="bedroom">Bedroom</option>
            <option value="office">Office</option>
          </select>
          {errors.category && <span className="error">{errors.category}</span>}
        </div>

        <div className="form-group">
          <label>Price (Rs.):</label>
          <input 
            type="number" 
            name="price" 
            value={productData.price} 
            onChange={handleInputChange} 
            min="0"
            step="0.01"
          />
          {errors.price && <span className="error">{errors.price}</span>}
        </div>

        <div className="form-group">
          <label>Material:</label>
          <input 
            type="text" 
            name="material" 
            value={productData.material} 
            onChange={handleInputChange} 
          />
          {errors.material && <span className="error">{errors.material}</span>}
        </div>

        <div className="form-group">
          <label>Stock Quantity:</label>
          <input 
            type="number" 
            name="stockQuantity" 
            value={productData.stockQuantity} 
            onChange={handleInputChange} 
            min="0"
          />
          {errors.stockQuantity && <span className="error">{errors.stockQuantity}</span>}
        </div>

        <div className="form-group">
          <label>Availability:</label>
          <select 
            name="availability" 
            value={productData.availability} 
            onChange={handleInputChange}
          >
            <option value={true}>Available</option>
            <option value={false}>Out of Stock</option>
          </select>
        </div>

        <div className="form-group">
          <label>Product Image:</label>
          <input 
            type="file" 
            name="image" 
            onChange={handleFileChange} 
            accept="image/*"
          />
        </div>

        <button 
          type="submit" 
          className="submit-button"
          disabled={isSubmitting}
        >
          {isSubmitting ? 'Adding...' : 'Add Product'}
        </button>
      </form>
    </div>
  );
};

export default AddProduct;
