import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import './AdminProduct.css';
import Header from './Header';
import Footer from './Footer';

const AdminProduct = () => {
  const [products, setProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("All");
  const [availabilityFilter, setAvailabilityFilter] = useState("All");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await axios.get("http://localhost:5000/products");
        const productsWithImages = response.data.products.map(product => ({
          ...product,
          imageUrl: product.image 
            ? `http://localhost:5000/images/products/${product.image}`
            : '/images/placeholder-product.jpg'
        }));
        setProducts(productsWithImages);
        setError(null);
      } catch (err) {
        console.error("Error fetching products:", err);
        setError("Failed to load products. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const handleDelete = async (productId, imageName) => {
    if (!window.confirm("Are you sure you want to delete this product?")) return;
    
    try {
      await axios.delete(`http://localhost:5000/products/${productId}`);
      setProducts(products.filter(product => product._id !== productId));
      alert("Product deleted successfully");
    } catch (err) {
      console.error("Error deleting product:", err);
      alert("Failed to delete product. Please try again.");
    }
  };

  const toggleAvailability = async (productId, currentAvailability) => {
    try {
      const response = await axios.put(`http://localhost:5000/products/${productId}`, {
        availability: !currentAvailability
      });
      
      setProducts(products.map(product => 
        product._id === productId 
          ? { ...product, availability: response.data.product.availability } 
          : product
      ));
    } catch (err) {
      console.error("Error updating availability:", err);
      alert("Failed to update availability. Please try again.");
    }
  };

  const filteredProducts = products.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = categoryFilter === "All" || product.category === categoryFilter;
    const matchesAvailability = 
      availabilityFilter === "All" || 
      (availabilityFilter === "Available" && product.availability) || 
      (availabilityFilter === "Not Available" && !product.availability);
    
    return matchesSearch && matchesCategory && matchesAvailability;
  });

  if (loading) return <div className="loading">Loading products...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="admin-dashboard">
      <Header />
      
      <div className="admin-content">
        <div className="admin-header">
          <h2>Product Management</h2>
          <div className="admin-actions">
            <button 
              className="add-product-btn"
              onClick={() => navigate('/admin/products/add')}
            >
              + Add Product
            </button>
          </div>
        </div>

        <div className="search-filter-container">
          <div className="search-box">
            <input
              type="text"
              placeholder="Search products..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
            <i className="search-icon">üîç</i>
          </div>

          <div className="filter-controls">
            <div className="filter-group">
              <label>Category:</label>
              <select
                value={categoryFilter}
                onChange={(e) => setCategoryFilter(e.target.value)}
                className="filter-dropdown"
              >
                <option value="All">All Categories</option>
                <option value="living room">Living Room</option>
                <option value="kitchen">Kitchen</option>
                <option value="bedroom">Bedroom</option>
                <option value="office">Office</option>
              </select>
            </div>

            <div className="filter-group">
              <label>Availability:</label>
              <select
                value={availabilityFilter}
                onChange={(e) => setAvailabilityFilter(e.target.value)}
                className="filter-dropdown"
              >
                <option value="All">All</option>
                <option value="Available">Available</option>
                <option value="Not Available">Not Available</option>
              </select>
            </div>
          </div>
        </div>

        <div className="product-grid">
          {filteredProducts.length > 0 ? (
            filteredProducts.map((product) => (
              <div key={product._id} className="product-card">
                <div className="product-image-container">
                  <img
                    src={product.imageUrl}
                    alt={product.name}
                    onError={(e) => {
                      e.target.src = '/images/placeholder-product.jpg';
                    }}
                  />
                  <div className="product-badge">
                    {product.availability ? (
                      <span className="in-stock">In Stock</span>
                    ) : (
                      <span className="out-of-stock">Out of Stock</span>
                    )}
                  </div>
                </div>

                <div className="product-info">
                  <h3 className="product-name">{product.name}</h3>
                  <div className="product-meta">
                    <span className="product-category">{product.category}</span>
                    <span className="product-price">Rs. {product.price.toFixed(2)}</span>
                    <span className="product-stock">Stock: {product.stockQuantity}</span>
                  </div>
                </div>

                <div className="product-actions">
                  <button
                    onClick={() => toggleAvailability(product._id, product.availability)}
                    className={`action-btn ${product.availability ? 'make-unavailable' : 'make-available'}`}
                  >
                    {product.availability ? 'Mark Unavailable' : 'Mark Available'}
                  </button>

                  <button
                    onClick={() => navigate(`/admin/products/edit/${product._id}`)}
                    className="action-btn edit-btn"
                  >
                    Edit
                  </button>

                  <button
                    onClick={() => handleDelete(product._id, product.image)}
                    className="action-btn delete-btn"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))
          ) : (
            <div className="no-products">
              <p>No products found matching your criteria</p>
              <button 
                className="reset-filters"
                onClick={() => {
                  setSearchQuery("");
                  setCategoryFilter("All");
                  setAvailabilityFilter("All");
                }}
              >
                Reset Filters
              </button>
            </div>
          )}
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default AdminProduct;
