import React, { useState } from "react";
import Nav from "../Nav/Nav";
import { useNavigate } from "react-router-dom";

function CreateItinerary({ userId, onSave }) {
  const navigate = useNavigate();
  const [tripName, setTripName] = useState("");
  const [destination, setDestination] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [totalBudget, setTotalBudget] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newItinerary = { userId, tripName, destination, startDate, endDate, totalBudget };

    try {
      const res = await fetch("http://localhost:5000/itineraries", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newItinerary),
      });

      const data = await res.json();
      onSave(data); 
      navigate("/my-itineraries"); 
    } catch (error) {
      console.error("Error saving itinerary:", error);
    }
  };

  return (
    <div>
      <Nav />
      <h3>Create Itinerary</h3>
      <form onSubmit={handleSubmit}>
        <label>Trip Name</label>
        <br/>
        <input type="text" value={tripName} onChange={(e) => setTripName(e.target.value)} required />
        <br/><br/>

        <label>Destination</label>
        <br/>
        <input type="text" value={destination} onChange={(e) => setDestination(e.target.value)} required />
        <br/><br/>

        <label>Start Date</label>
        <br/>
        <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} required />
        <br/><br/>

        <label>End Date</label>
        <br/>
        <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} required />
        <br/><br/>

        <label>Total Budget</label>
        <br/>
        <input type="number" value={totalBudget} onChange={(e) => setTotalBudget(e.target.value)} required />
        <br/><br/>

        <button type="submit">Save Itinerary</button>
      </form>
    </div>
  );
}

export default CreateItinerary;
