import React, { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../Context/AuthContext";
import { Link, useNavigate } from "react-router-dom";
import { getProductImageUrl, handleImageError } from "../utils/imageUtils";
import "./Cart.css";

const Cart = () => {
  const { user } = useAuth();
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [updatingItems, setUpdatingItems] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }

    const fetchCart = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`http://localhost:5000/cart/${user._id}`);
        setCart(response.data);
        setError(null);
      } catch (error) {
        console.error("Error fetching cart:", error);
        setError("Failed to load your cart. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, [user, navigate]);

  const updateQuantity = async (productId, newQuantity) => {
    if (newQuantity < 1 || newQuantity > 10) return;
    
    try {
      setUpdatingItems(prev => ({ ...prev, [productId]: true }));
      await axios.put(`http://localhost:5000/cart/${user._id}/${productId}`, {
        quantity: newQuantity
      });
      
      const response = await axios.get(`http://localhost:5000/cart/${user._id}`);
      setCart(response.data);
    } catch (error) {
      console.error("Error updating quantity:", error);
      alert("Failed to update quantity. Please try again.");
    } finally {
      setUpdatingItems(prev => ({ ...prev, [productId]: false }));
    }
  };

  const removeItem = async (productId) => {
    if (!window.confirm("Are you sure you want to remove this item?")) return;
    
    try {
      setUpdatingItems(prev => ({ ...prev, [productId]: true }));
      await axios.delete(`http://localhost:5000/cart/${user._id}/${productId}`);
      
      const response = await axios.get(`http://localhost:5000/cart/${user._id}`);
      setCart(response.data);
    } catch (error) {
      console.error("Error removing item:", error);
      alert("Failed to remove item. Please try again.");
    } finally {
      setUpdatingItems(prev => ({ ...prev, [productId]: false }));
    }
  };

  const clearCart = async () => {
    if (!window.confirm("Are you sure you want to clear your cart?")) return;
    
    try {
      await axios.delete(`http://localhost:5000/cart/${user._id}/clear`);
      setCart({ ...cart, items: [] });
    } catch (error) {
      console.error("Error clearing cart:", error);
      alert("Failed to clear cart. Please try again.");
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading your cart...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <div className="error-message">{error}</div>
        <button className="back-button" onClick={() => navigate('/products')}>
          Continue Shopping
        </button>
      </div>
    );
  }

  if (!cart || cart.items.length === 0) {
    return (
      <div className="empty-cart">
        <h2>Your Cart is Empty</h2>
        <p>Looks like you haven't added any items to your cart yet.</p>
        <Link to="/products" className="continue-shopping">
          Continue Shopping
        </Link>
      </div>
    );
  }

  const totalPrice = cart.items.reduce(
    (sum, item) => sum + (item.productId.price * item.quantity),
    0
  );

  return (
    <div className="cart-container">
      <div className="cart-header">
        <h2>Your Shopping Cart</h2>
        <button 
          onClick={clearCart}
          className="clear-cart-btn"
          disabled={cart.items.length === 0}
        >
          Clear Cart
        </button>
      </div>
      
      <div className="cart-content">
        <div className="cart-items">
          {cart.items.map((item) => (
            <div key={item.productId._id} className="cart-item">
              <div className="item-image">
                <img 
                  src={getProductImageUrl(item.productId.image)} 
                  alt={item.productId.name} 
                  onError={handleImageError}
                />
              </div>
              
              <div className="item-details">
                <h3>
                  <Link to={`/products/${item.productId._id}`}>
                    {item.productId.name}
                  </Link>
                </h3>
                <p className="item-price">Rs. {item.productId.price.toFixed(2)}</p>
                
                <div className="quantity-control">
                  <button 
                    onClick={() => updateQuantity(item.productId._id, item.quantity - 1)}
                    disabled={item.quantity <= 1 || updatingItems[item.productId._id]}
                  >
                    -
                  </button>
                  <span>{item.quantity}</span>
                  <button 
                    onClick={() => updateQuantity(item.productId._id, item.quantity + 1)}
                    disabled={item.quantity >= 10 || updatingItems[item.productId._id]}
                  >
                    +
                  </button>
                </div>
                
                <p className="item-total">
                  Total: Rs. {(item.productId.price * item.quantity).toFixed(2)}
                </p>
                
                <button 
                  onClick={() => removeItem(item.productId._id)}
                  className="remove-item-btn"
                  disabled={updatingItems[item.productId._id]}
                >
                  {updatingItems[item.productId._id] ? 'Removing...' : 'Remove'}
                </button>
              </div>
            </div>
          ))}
        </div>
        
        <div className="cart-summary">
          <h3>Order Summary</h3>
          <div className="summary-row">
            <span>Subtotal ({cart.items.length} items):</span>
            <span>Rs. {totalPrice.toFixed(2)}</span>
          </div>
          <div className="summary-row">
            <span>Shipping:</span>
            <span>FREE</span>
          </div>
          <div className="summary-row total">
            <span>Total:</span>
            <span>Rs. {totalPrice.toFixed(2)}</span>
          </div>
          
          <Link 
            to="/checkout" 
            className="checkout-btn"
            disabled={cart.items.length === 0}
          >
            Proceed to Checkout
          </Link>
          
          <Link to="/products" className="continue-shopping">
            Continue Shopping
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Cart;
