import React, { useState, useEffect } from "react";
import { useAuth } from "../Context/AuthContext";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { getProductImageUrl, handleImageError } from "../utils/imageUtils";
import "./Cart.css";

const Cart = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!user) {
      navigate("/login");
      return;
    }

    const fetchCart = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`http://localhost:5000/cart/${user._id}`);
        setCart(response.data);
        setError(null);
      } catch (err) {
        console.error("Error fetching cart:", err);
        setError("Failed to load your cart");
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, [user, navigate]);

  const updateQuantity = async (productId, newQuantity) => {
    if (newQuantity < 1) return;

    try {
      const product = cart.items.find(item => item.productId._id === productId)?.productId;
      
      if (newQuantity > product.stockQuantity) {
        alert(`Only ${product.stockQuantity} available in stock`);
        return;
      }

      await axios.put(`http://localhost:5000/cart/${user._id}/${productId}`, {
        quantity: newQuantity,
      });
      
      const response = await axios.get(`http://localhost:5000/cart/${user._id}`);
      setCart(response.data);
    } catch (err) {
      console.error("Error updating quantity:", err);
      alert("Failed to update quantity. Please try again.");
    }
  };

  const removeItem = async (productId) => {
    try {
      await axios.delete(`http://localhost:5000/cart/${user._id}/${productId}`);
      const response = await axios.get(`http://localhost:5000/cart/${user._id}`);
      setCart(response.data);
    } catch (err) {
      console.error("Error removing item:", err);
      alert("Failed to remove item. Please try again.");
    }
  };

  const clearCart = async () => {
    try {
      await axios.delete(`http://localhost:5000/cart/${user._id}/clear`);
      setCart({ ...cart, items: [] });
    } catch (err) {
      console.error("Error clearing cart:", err);
      alert("Failed to clear cart. Please try again.");
    }
  };

  if (loading) return <div className="loading">Loading your cart...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!cart || cart.items.length === 0) {
    return (
      <div className="empty-cart">
        <h2>Your Cart is Empty</h2>
        <Link to="/products" className="continue-shopping">
          Continue Shopping
        </Link>
      </div>
    );
  }

  const totalPrice = cart.items.reduce(
    (sum, item) => sum + item.productId.price * item.quantity,
    0
  );

  return (
    <div className="cart-container">
      <h2>Your Shopping Cart</h2>

      <div className="cart-items">
        {cart.items.map((item) => (
          <div key={item.productId._id} className="cart-item">
            <div className="item-image-container">
              <img
                src={getProductImageUrl(item.productId.image)}
                alt={item.productId.name}
                onError={handleImageError}
                className="cart-item-image"
              />
            </div>

            <div className="item-details">
              <h3>
                <Link to={`/products/${item.productId._id}`}>
                  {item.productId.name}
                </Link>
              </h3>
              <p>Price: Rs. {item.productId.price.toFixed(2)}</p>
              
              <div className={`stock-info ${item.productId.stockQuantity > 0 ? 'in-stock' : 'out-of-stock'}`}>
                {item.productId.stockQuantity > 0 ? (
                  `In Stock (${item.productId.stockQuantity} available)`
                ) : (
                  "Out of Stock"
                )}
              </div>

              <div className="quantity-control">
                <button
                  onClick={() => updateQuantity(item.productId._id, item.quantity - 1)}
                  disabled={item.quantity <= 1}
                >
                  -
                </button>
                <span>{item.quantity}</span>
                <button
                  onClick={() => updateQuantity(item.productId._id, item.quantity + 1)}
                  disabled={item.quantity >= item.productId.stockQuantity}
                >
                  +
                </button>
              </div>

              <p className="item-total">
                Total: Rs. {(item.productId.price * item.quantity).toFixed(2)}
              </p>

              <button
                onClick={() => removeItem(item.productId._id)}
                className="remove-item"
              >
                Remove
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="cart-summary">
        <h3>Order Summary</h3>
        <div className="summary-row">
          <span>Subtotal:</span>
          <span>Rs. {totalPrice.toFixed(2)}</span>
        </div>
        <div className="summary-row">
          <span>Shipping:</span>
          <span>Free</span>
        </div>
        <div className="summary-row total">
          <span>Total:</span>
          <span>Rs. {totalPrice.toFixed(2)}</span>
        </div>

        <div className="cart-actions">
          <button onClick={clearCart} className="clear-cart">
            Clear Cart
          </button>
          <Link to="/checkout" className="checkout-button">
            Proceed to Checkout
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Cart;
