import React, { useState, useEffect } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import { useAuth } from "../Context/AuthContext";
import axios from "axios";
import { getProductImageUrl, handleImageError } from "../utils/imageUtils";
import "./ProductDetails.css";

const ProductDetails = () => {
  const { id } = useParams();
  const { state } = useLocation();
  const { user } = useAuth();
  const navigate = useNavigate();
  const [product, setProduct] = useState(state?.product || null);
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(!state?.product);
  const [error, setError] = useState(null);
  const [stockStatus, setStockStatus] = useState("");

  useEffect(() => {
    if (state?.product) return;

    const fetchProduct = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`http://localhost:5000/products/${id}`);
        setProduct(response.data.product);
        setError(null);
      } catch (err) {
        console.error("Error fetching product:", err);
        setError("Failed to load product details");
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id, state]);

  useEffect(() => {
    if (product) {
      setStockStatus(
        product.stockQuantity > 0 
          ? `In Stock (${product.stockQuantity} available)` 
          : "Out of Stock"
      );
    }
  }, [product]);

  const handleAddToCart = async () => {
    if (!user) {
      navigate("/login");
      return;
    }

    if (product.stockQuantity < quantity) {
      alert(`Only ${product.stockQuantity} items available in stock`);
      return;
    }

    try {
      await axios.post("http://localhost:5000/cart", {
        userId: user._id,
        productId: id,
        quantity: quantity,
      });
      alert(`${quantity} ${product.name}(s) added to cart!`);
    } catch (err) {
      console.error("Error adding to cart:", err);
      alert(
        err.response?.data?.error ||
        "Failed to add to cart. Please try again."
      );
    }
  };

  if (loading) return <div className="loading">Loading product details...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!product) return <div className="error">Product not found</div>;

  return (
    <div className="product-details-container">
      <div className="product-image-container">
        <img
          src={getProductImageUrl(product.image)}
          alt={product.name}
          className="product-image"
          onError={handleImageError}
        />
      </div>

      <div className="product-info">
        <h1>{product.name}</h1>
        <p className="product-category">{product.category}</p>
        
        <div className="price-section">
          <span className="price">Rs. {product.price.toFixed(2)}</span>
          {product.originalPrice && (
            <span className="original-price">
              Rs. {product.originalPrice.toFixed(2)}
            </span>
          )}
        </div>

        <p className={`stock-status ${product.stockQuantity > 0 ? 'in-stock' : 'out-of-stock'}`}>
          {stockStatus}
        </p>

        {product.material && (
          <p className="material">
            <strong>Material:</strong> {product.material}
          </p>
        )}

        {product.description && (
          <div className="description">
            <h3>Description</h3>
            <p>{product.description}</p>
          </div>
        )}

        {product.stockQuantity > 0 && (
          <div className="quantity-selector">
            <label>Quantity:</label>
            <div className="quantity-controls">
              <button 
                onClick={() => setQuantity(prev => Math.max(1, prev - 1))}
                disabled={quantity <= 1}
              >
                -
              </button>
              <span>{quantity}</span>
              <button 
                onClick={() => setQuantity(prev => Math.min(product.stockQuantity, prev + 1))}
                disabled={quantity >= product.stockQuantity}
              >
                +
              </button>
            </div>
          </div>
        )}

        <div className="action-buttons">
          {product.stockQuantity > 0 ? (
            <>
              <button 
                className="add-to-cart" 
                onClick={handleAddToCart}
              >
                Add to Cart
              </button>
              <button 
                className="buy-now"
                onClick={() => {
                  handleAddToCart();
                  navigate("/checkout");
                }}
              >
                Buy Now
              </button>
            </>
          ) : (
            <button className="notify-me" onClick={() => alert("We'll notify you when this product is back in stock")}>
              Notify When Available
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
