import React, { useState } from "react";
import Nav from "../Nav/Nav";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function CreateItinerary() {
  const history = useNavigate();
  const [inputs, setInputs] = useState({
    tripName: "",
    destination: "",
    startDate: "",
    endDate: "",
    totalBudget: "",
  });

  const handleChange = (e) => {
    setInputs((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(inputs);
    sendRequest().then(() => history("/my-itineraries"));
  };

  const sendRequest = async () => {
    await axios.post("http://localhost:5000/itineraries", {
      tripName: String(inputs.tripName),
      destination: String(inputs.destination),
      startDate: String(inputs.startDate),
      endDate: String(inputs.endDate),
      totalBudget: Number(inputs.totalBudget),
    }).then(res => res.data);
  };

  return (
    <div>
      <Nav />
      <h3>Create Itinerary</h3>
      <form onSubmit={handleSubmit}>
        <label>Trip Name</label>
        <br />
        <input type="text" name="tripName" onChange={handleChange} value={inputs.tripName} required />
        <br /><br />
        
        <label>Destination</label>
        <br />
        <input type="text" name="destination" onChange={handleChange} value={inputs.destination} required />
        <br /><br />
        
        <label>Start Date</label>
        <br />
        <input type="date" name="startDate" onChange={handleChange} value={inputs.startDate} required />
        <br /><br />
        
        <label>End Date</label>
        <br />
        <input type="date" name="endDate" onChange={handleChange} value={inputs.endDate} required />
        <br /><br />
        
        <label>Total Budget</label>
        <br />
        <input type="number" name="totalBudget" onChange={handleChange} value={inputs.totalBudget} required />
        <br /><br />
        
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default CreateItinerary;
