import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { addItemToWishlist, removeFromWishlist } from '../services/wishlistService';
import { addToCart } from '../services/cartService';
import './ProductDetails.css';

const ProductDetails = () => {
    const { id } = useParams();
    const { user } = useAuth();
    const navigate = useNavigate();
    const [product, setProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isInWishlist, setIsInWishlist] = useState(false);
    const [wishlistLoading, setWishlistLoading] = useState(false);
    const [cartLoading, setCartLoading] = useState(false);

    useEffect(() => {
        const fetchProductDetails = async () => {
            try {
                setLoading(true);
                const response = await fetch(`http://localhost:5000/products/${id}`);
                const data = await response.json();
                
                if (!response.ok) {
                    throw new Error(data.error || 'Failed to fetch product');
                }

                setProduct(data.product);
                setError('');

                // Check if product is in wishlist
                if (user) {
                    const { success, items } = await getWishlist(user._id);
                    if (success) {
                        setIsInWishlist(items.some(item => item.productId._id === id));
                    }
                }
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchProductDetails();
    }, [id, user]);

    const handleWishlistAction = async () => {
        if (!user) {
            navigate('/login', { state: { from: `/products/${id}` } });
            return;
        }

        try {
            setWishlistLoading(true);
            if (isInWishlist) {
                const { success } = await removeFromWishlist(user._id, id);
                if (success) setIsInWishlist(false);
            } else {
                const { success } = await addItemToWishlist(user._id, id);
                if (success) setIsInWishlist(true);
            }
        } catch (err) {
            setError('Failed to update wishlist. Please try again.');
        } finally {
            setWishlistLoading(false);
        }
    };

    const handleAddToCart = async () => {
        if (!user) {
            navigate('/login', { state: { from: `/products/${id}` } });
            return;
        }

        try {
            setCartLoading(true);
            const { success, error } = await addToCart(user._id, id, quantity);
            
            if (success) {
                alert(`${quantity} ${product.name}(s) added to cart!`);
            } else {
                setError(error || 'Failed to add to cart');
            }
        } catch (err) {
            setError('Failed to add to cart. Please try again.');
        } finally {
            setCartLoading(false);
        }
    };

    const handleBuyNow = async () => {
        if (!user) {
            navigate('/login', { state: { from: `/products/${id}` } });
            return;
        }

        try {
            setCartLoading(true);
            const { success } = await addToCart(user._id, id, quantity);
            
            if (success) {
                navigate('/checkout');
            } else {
                setError('Failed to proceed to checkout');
            }
        } catch (err) {
            setError('Failed to proceed to checkout. Please try again.');
        } finally {
            setCartLoading(false);
        }
    };

    if (loading) {
        return (
            <div className="product-details-loading">
                <div className="loading-spinner"></div>
                <p>Loading product details...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="product-details-error">
                <div className="error-icon">!</div>
                <h3>Error Loading Product</h3>
                <p>{error}</p>
                <button 
                    onClick={() => window.location.reload()}
                    className="retry-button"
                >
                    Try Again
                </button>
                <button 
                    onClick={() => navigate('/products')}
                    className="browse-button"
                >
                    Browse Products
                </button>
            </div>
        );
    }

    if (!product) {
        return (
            <div className="product-not-found">
                <h2>Product Not Found</h2>
                <p>The product you're looking for doesn't exist or may have been removed.</p>
                <button 
                    onClick={() => navigate('/products')}
                    className="browse-button"
                >
                    Browse Products
                </button>
            </div>
        );
    }

    return (
        <div className="product-details-container">
            <div className="product-image-gallery">
                <div className="main-image">
                    <img 
                        src={product.image || '/placeholder-product.jpg'} 
                        alt={product.name}
                        onError={(e) => {
                            e.target.src = '/placeholder-product.jpg';
                        }}
                    />
                </div>
            </div>

            <div className="product-info">
                <h1 className="product-title">{product.name}</h1>
                
                {product.brand && (
                    <p className="product-brand">Brand: {product.brand}</p>
                )}

                <div className="price-section">
                    <span className="current-price">
                        Rs. {product.price.toFixed(2)}
                    </span>
                    {product.originalPrice && (
                        <span className="original-price">
                            Rs. {product.originalPrice.toFixed(2)}
                        </span>
                    )}
                    {product.originalPrice && (
                        <span className="discount-badge">
                            {Math.round(
                                ((product.originalPrice - product.price) / product.originalPrice) * 100
                            )}% OFF
                        </span>
                    )}
                </div>

                <div className="product-meta">
                    <div className={`availability ${product.availability ? 'in-stock' : 'out-of-stock'}`}>
                        {product.availability ? 'In Stock' : 'Out of Stock'}
                    </div>

                    {product.ratings && (
                        <div className="ratings">
                            {[...Array(5)].map((_, i) => (
                                <span 
                                    key={i}
                                    className={`star ${i < Math.floor(product.ratings) ? 'filled' : ''}`}
                                >
                                    ★
                                </span>
                            ))}
                            <span className="rating-count">({product.ratingCount || 0})</span>
                        </div>
                    )}
                </div>

                {product.description && (
                    <div className="product-description">
                        <h3>Description</h3>
                        <p>{product.description}</p>
                    </div>
                )}

                {product.specifications && (
                    <div className="product-specs">
                        <h3>Specifications</h3>
                        <ul>
                            {Object.entries(product.specifications).map(([key, value]) => (
                                <li key={key}>
                                    <strong>{key}:</strong> {value}
                                </li>
                            ))}
                        </ul>
                    </div>
                )}

                {product.availability && (
                    <div className="quantity-selector">
                        <label>Quantity:</label>
                        <div className="quantity-controls">
                            <button 
                                onClick={() => setQuantity(prev => Math.max(1, prev - 1))}
                                disabled={quantity <= 1}
                            >
                                -
                            </button>
                            <input
                                type="number"
                                value={quantity}
                                min="1"
                                max="10"
                                onChange={(e) => {
                                    const val = parseInt(e.target.value) || 1;
                                    setQuantity(Math.max(1, Math.min(10, val)));
                                }}
                            />
                            <button 
                                onClick={() => setQuantity(prev => Math.min(10, prev + 1))}
                                disabled={quantity >= 10}
                            >
                                +
                            </button>
                        </div>
                    </div>
                )}

                <div className="action-buttons">
                    {product.availability && (
                        <>
                            <button
                                className="add-to-cart"
                                onClick={handleAddToCart}
                                disabled={cartLoading}
                            >
                                {cartLoading ? 'Adding...' : 'Add to Cart'}
                            </button>
                            <button
                                className="buy-now"
                                onClick={handleBuyNow}
                                disabled={cartLoading}
                            >
                                {cartLoading ? 'Processing...' : 'Buy Now'}
                            </button>
                        </>
                    )}
                    <button
                        className={`wishlist-button ${isInWishlist ? 'in-wishlist' : ''}`}
                        onClick={handleWishlistAction}
                        disabled={wishlistLoading}
                    >
                        {wishlistLoading ? (
                            <span className="button-spinner"></span>
                        ) : isInWishlist ? (
                            '✓ In Wishlist'
                        ) : (
                            '♡ Add to Wishlist'
                        )}
                    </button>
                    <button
                        className="back-button"
                        onClick={() => navigate(-1)}
                    >
                        Back to Products
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ProductDetails;
