import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../Context/AuthContext";
import api from "../utils/api"; // Use your configured axios instance
import "./OrderConfirmation.css";

const OrderConfirmation = () => {
  const { orderId } = useParams();
  const { user, token } = useAuth(); // Get both user and token
  const navigate = useNavigate();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const response = await api.get(`/orders/${orderId}`); // api already includes auth header
        
        // Verify order belongs to current user
        if (response.data.userId.toString() !== user._id) {
          navigate('/');
          return;
        }
        
        setOrder(response.data);
      } catch (err) {
        if (err.response?.status === 401) {
          setError('Session expired. Please login again.');
        } else {
          setError(err.response?.data?.message || 'Failed to load order details');
        }
      } finally {
        setLoading(false);
      }
    };

    if (user && token) {
      fetchOrder();
    } else {
      navigate('/login');
    }
  }, [orderId, user, token, navigate]);

  // Handle image loading errors
  const handleImageError = (e) => {
    e.target.src = '/placeholder-product.jpg';
  };

  if (loading) return <div className="loading">Loading order details...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!order) return <div className="error">Order not found</div>;

  return (
    <div className="order-confirmation">
      <div className="confirmation-header">
        <h1>Order Confirmation</h1>
        <p>Thank you for your order!</p>
        <p>Your order number is: <strong>#{order._id}</strong></p>
      </div>
      
      <div className="order-details">
        <div className="order-summary">
          <h2>Order Summary</h2>
          <div className="order-items">
            {order.items.map((item, index) => (
              <div key={index} className="order-item">
                <img 
                  src={item.productId.image?.startsWith('http') 
                    ? item.productId.image 
                    : `/images/${item.productId.image}`}
                  alt={item.productId.name}
                  onError={handleImageError}
                />
                <div className="item-details">
                  <h4>{item.productId.name}</h4>
                  <p>Quantity: {item.quantity}</p>
                  <p>Price: Rs. {item.price.toFixed(2)}</p>
                  <p>Total: Rs. {(item.price * item.quantity).toFixed(2)}</p>
                </div>
              </div>
            ))}
          </div>
          <div className="order-total">
            <h3>Total: Rs. {order.totalPrice.toFixed(2)}</h3>
          </div>
        </div>
        
        <div className="shipping-details">
          <h2>Shipping Details</h2>
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Address:</strong> {order.shippingAddress.address}</p>
          <p><strong>City:</strong> {order.shippingAddress.city}</p>
          <p><strong>Postal Code:</strong> {order.shippingAddress.postalCode}</p>
          <p><strong>Payment Method:</strong> Cash on Delivery</p>
        </div>
      </div>
      
      <div className="next-steps">
        <h2>What's Next?</h2>
        <p>You'll receive an email confirmation shortly. We'll notify you when your order ships.</p>
        <button onClick={() => navigate('/products')}>Continue Shopping</button>
      </div>
    </div>
  );
};

export default OrderConfirmation;
