import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../Context/AuthContext";
import api from "../utils/api";
import "./Checkout.css";

const Checkout = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({
    name: user?.name || "",
    email: user?.email || "",
    address: "",
    city: "",
    postalCode: "",
    paymentMethod: "cashOnDelivery"
  });

  // Fetch cart data
  useEffect(() => {
    const fetchCart = async () => {
      try {
        const response = await api.get(`/cart/${user._id}`);
        setCart(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load cart');
      } finally {
        setLoading(false);
      }
    };

    if (user) {
      fetchCart();
    }
  }, [user]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const orderData = {
        userId: user._id,
        items: cart.items,
        shippingAddress: {
          address: formData.address,
          city: formData.city,
          postalCode: formData.postalCode
        },
        paymentMethod: formData.paymentMethod
      };

      const response = await api.post('/orders', orderData);
      await api.delete(`/cart/${user._id}/clear`);
      navigate(`/order-confirmation/${response.data._id}`);
    } catch (err) {
      setError(err.response?.data?.message || 'Checkout failed. Please try again.');
    }
  };

  // Loading state
  if (loading) return <div className="loading">Loading checkout...</div>;
  
  // Error state
  if (error) return <div className="error">{error}</div>;
  
  // Empty cart state
  if (!cart || cart.items.length === 0) {
    return (
      <div className="empty-cart">
        <h2>Your cart is empty</h2>
        <button onClick={() => navigate('/products')}>Continue Shopping</button>
      </div>
    );
  }

  const totalPrice = cart.items.reduce(
    (sum, item) => sum + (item.productId.price * item.quantity),
    0
  );

  return (
    <div className="checkout-container">
      <div className="checkout-form">
        {/* Form section remains the same */}
      </div>
      
      <div className="order-summary">
        <h2>Order Summary</h2>
        <div className="order-items">
          {cart.items.map(item => (
            <div key={item.productId._id} className="order-item">
              <img 
                src={item.productId.image || '/placeholder-image.jpg'} 
                alt={item.productId.name} 
              />
              <div>
                <h4>{item.productId.name}</h4>
                <p>Rs. {item.productId.price} Ã— {item.quantity}</p>
              </div>
            </div>
          ))}
        </div>
        <div className="order-total">
          <h3>Total: Rs. {totalPrice.toFixed(2)}</h3> {/* Added totalPrice display */}
        </div>
      </div>
    </div>
  );
};

export default Checkout;
