// src/utils/imageUtils.js
export const getProductImageUrl = (imagePath) => {
  if (!imagePath) return '/placeholder-product.jpg';
  
  // If it's already a full URL or data URL, return as-is
  if (imagePath.startsWith('http') || imagePath.startsWith('data:')) {
    return imagePath;
  }
  
  // Handle cases where the path might already include /images/products/
  if (imagePath.includes('/images/products/')) {
    return `http://localhost:5000${imagePath.startsWith('/') ? '' : '/'}${imagePath}`;
  }
  
  // Default case - prepend the base path
  return `http://localhost:5000/images/products/${imagePath}`;
};

export const handleImageError = (e) => {
  console.error('Image failed to load:', e.target.src);
  e.target.src = '/placeholder-product.jpg';
  e.target.style.objectFit = 'contain'; // Ensures placeholder fits well
};
