import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../Context/AuthContext";
import { 
    addItemToWishlist, 
    removeFromWishlist 
} from "../Services/wishlistService";
import './ProductDetails.css';

const ProductDetails = () => {
    const { id } = useParams();
    const { user } = useAuth();
    const navigate = useNavigate();
    const [product, setProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isInWishlist, setIsInWishlist] = useState(false);
    const [wishlistLoading, setWishlistLoading] = useState(false);

    useEffect(() => {
        const fetchProductDetails = async () => {
            try {
                setLoading(true);
                const response = await fetch(`http://localhost:5000/products/${id}`);
                const data = await response.json();
                
                if (!response.ok) throw new Error(data.error || "Product not found");
                
                setProduct(data.product);
                setError('');
                
                // Check wishlist status if user is logged in
                if (user) {
                    const { success, items } = await getWishlist(user._id);
                    if (success) {
                        setIsInWishlist(items.some(item => item.productId._id === id));
                    }
                }
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchProductDetails();
    }, [id, user]);

    const handleWishlistToggle = async () => {
        if (!user) {
            navigate('/login');
            return;
        }

        try {
            setWishlistLoading(true);
            if (isInWishlist) {
                const { success } = await removeFromWishlist(user._id, id);
                if (success) setIsInWishlist(false);
            } else {
                const { success } = await addItemToWishlist(user._id, id);
                if (success) setIsInWishlist(true);
            }
        } catch (err) {
            setError('Failed to update wishlist');
        } finally {
            setWishlistLoading(false);
        }
    };

    const handleAddToCart = async () => {
        // Your existing cart implementation
    };

    const handleBuyNow = async () => {
        // Your existing buy now implementation
    };

    if (loading) {
        return (
            <div className="product-loading">
                <div className="spinner"></div>
                <p>Loading product details...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="product-error">
                <p>{error}</p>
                <button onClick={() => window.location.reload()}>Try Again</button>
            </div>
        );
    }

    if (!product) {
        return (
            <div className="product-not-found">
                <h2>Product Not Found</h2>
                <button onClick={() => navigate('/products')}>Browse Products</button>
            </div>
        );
    }

    return (
        <div className="product-details-container">
            <div className="product-details">
                <div className="product-info">
                    <h1>{product.name}</h1>
                    {product.brand && <p className="brand">Brand: {product.brand}</p>}
                    
                    <div className="price-section">
                        <span className="current-price">Rs. {product.price.toFixed(2)}</span>
                        {product.originalPrice && (
                            <span className="original-price">Rs. {product.originalPrice.toFixed(2)}</span>
                        )}
                    </div>

                    <div className="meta-info">
                        <p><strong>Category:</strong> {product.category}</p>
                        <p className={`stock ${product.availability ? 'in-stock' : 'out-of-stock'}`}>
                            {product.availability ? 'In Stock' : 'Out of Stock'}
                        </p>
                    </div>

                    {product.description && (
                        <div className="description">
                            <h3>Description</h3>
                            <p>{product.description}</p>
                        </div>
                    )}

                    {product.availability && (
                        <div className="quantity-selector">
                            <label>Quantity:</label>
                            <div className="quantity-controls">
                                <button 
                                    onClick={() => setQuantity(q => Math.max(1, q - 1))}
                                    disabled={quantity <= 1}
                                >
                                    -
                                </button>
                                <input 
                                    type="number" 
                                    value={quantity}
                                    min="1"
                                    max="10"
                                    onChange={(e) => {
                                        const val = Math.max(1, Math.min(10, parseInt(e.target.value) || 1));
                                        setQuantity(val);
                                    }}
                                />
                                <button 
                                    onClick={() => setQuantity(q => Math.min(10, q + 1))}
                                    disabled={quantity >= 10}
                                >
                                    +
                                </button>
                            </div>
                        </div>
                    )}

                    <div className="action-buttons">
                        {product.availability && (
                            <>
                                <button 
                                    className="add-to-cart"
                                    onClick={handleAddToCart}
                                >
                                    Add to Cart
                                </button>
                                <button 
                                    className="buy-now"
                                    onClick={handleBuyNow}
                                >
                                    Buy Now
                                </button>
                            </>
                        )}
                        <button
                            className={`wishlist-btn ${isInWishlist ? 'active' : ''}`}
                            onClick={handleWishlistToggle}
                            disabled={wishlistLoading}
                        >
                            {wishlistLoading ? (
                                <span className="btn-spinner"></span>
                            ) : isInWishlist ? (
                                '✓ In Wishlist'
                            ) : (
                                '♡ Add to Wishlist'
                            )}
                        </button>
                        <button 
                            className="back-btn"
                            onClick={() => navigate(-1)}
                        >
                            Back to Products
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductDetails;
