import api from '../utils/api';

const WISHLIST_BASE_URL = '/wishlists'; // Now using plural form

export const wishlistService = {
  /**
   * Add item to wishlist
   * @param {string} userId 
   * @param {string} productId 
   * @returns {Promise<{success: boolean, data?: any, message?: string, error?: string}>}
   */
  async addItem(userId, productId) {
    try {
      const response = await api.post(`${WISHLIST_BASE_URL}/add`, {
        userId,
        productId
      });
      return {
        success: true,
        data: response.data,
        message: response.data.message || 'Item added to wishlist'
      };
    } catch (error) {
      console.error('Add to wishlist error:', error.response?.data || error.message);
      return {
        success: false,
        error: error.response?.data?.message || 'Failed to add to wishlist'
      };
    }
  },

  /**
   * Get user's wishlist
   * @param {string} userId 
   * @returns {Promise<{success: boolean, items: Array, wishlist?: object, error?: string}>}
   */
  async getWishlist(userId) {
    try {
      const response = await api.get(`${WISHLIST_BASE_URL}/user/${userId}`);
      return {
        success: true,
        items: response.data.items || [],
        wishlist: response.data.wishlist || { items: [] }
      };
    } catch (error) {
      console.error('Get wishlist error:', error.response?.data || error.message);
      return {
        success: false,
        items: [],
        error: error.response?.data?.message || 'Failed to fetch wishlist'
      };
    }
  },

  /**
   * Remove item from wishlist
   * @param {string} userId 
   * @param {string} productId 
   * @returns {Promise<{success: boolean, data?: any, message?: string, error?: string}>}
   */
  async removeItem(userId, productId) {
    try {
      const response = await api.delete(`${WISHLIST_BASE_URL}/remove/${userId}/${productId}`);
      return {
        success: true,
        data: response.data,
        message: response.data.message || 'Item removed from wishlist'
      };
    } catch (error) {
      console.error('Remove from wishlist error:', error.response?.data || error.message);
      return {
        success: false,
        error: error.response?.data?.message || 'Failed to remove from wishlist'
      };
    }
  },

  /**
   * Clear entire wishlist
   * @param {string} userId 
   * @returns {Promise<{success: boolean, data?: any, message?: string, error?: string}>}
   */
  async clearWishlist(userId) {
    try {
      const response = await api.delete(`${WISHLIST_BASE_URL}/clear/${userId}`);
      return {
        success: true,
        data: response.data,
        message: response.data.message || 'Wishlist cleared'
      };
    } catch (error) {
      console.error('Clear wishlist error:', error.response?.data || error.message);
      return {
        success: false,
        error: error.response?.data?.message || 'Failed to clear wishlist'
      };
    }
  }
};
