export const getProductImageUrl = (imagePath) => {
  // Default placeholder image
  const placeholder = '/images/placeholder-product.jpg';
  
  if (!imagePath) return placeholder;
  
  // If it's already a full URL or data URL, return as-is
  if (imagePath.startsWith('http') || imagePath.startsWith('data:')) {
    return imagePath;
  }
  
  // Handle local paths
  if (imagePath.startsWith('/images/products/')) {
    return imagePath;
  }
  
  // Handle cases where path might be malformed
  return `/images/products/${imagePath.replace(/^\/+/, '')}`;
};

export const handleImageError = (e) => {
  const placeholder = '/images/placeholder-product.jpg';
  
  // If we're already showing the placeholder, do nothing
  if (e.target.src.includes('placeholder-product.jpg')) return;
  
  console.error('Image failed to load:', e.target.src);
  e.target.src = placeholder;
  e.target.onerror = null; // Prevent infinite loop
  e.target.style.objectFit = 'contain';
};
