import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const UpdateItinerary = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [itinerary, setItinerary] = useState({
    tripName: "",
    destination: "",
    startDate: "",
    endDate: "",
    totalBudget: "",
  });
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchItinerary = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/itineraries/${id}`);
        setItinerary(response.data.itinerary);
      } catch (error) {
        console.log("Error fetching itinerary:", error);
      }
    };

    fetchItinerary();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.put(`http://localhost:5000/itineraries/${id}`, itinerary);
      if (response.status === 200) {
        alert("Itinerary Updated Successfully");
        navigate(`/myItinerary/${id}`);
      }
    } catch (error) {
      setError("Failed to update itinerary. Please try again.");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setItinerary((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <div>
      <h2>Update Itinerary</h2>
      <form onSubmit={handleSubmit}>
        {error && <p>{error}</p>}
        <input
          type="text"
          name="tripName"
          value={itinerary.tripName}
          onChange={handleChange}
        />
        <input
          type="text"
          name="destination"
          value={itinerary.destination}
          onChange={handleChange}
        />
        <input
          type="date"
          name="startDate"
          value={itinerary.startDate}
          onChange={handleChange}
        />
        <input
          type="date"
          name="endDate"
          value={itinerary.endDate}
          onChange={handleChange}
        />
        <input
          type="number"
          name="totalBudget"
          value={itinerary.totalBudget}
          onChange={handleChange}
        />
        <button type="submit">Update</button>
      </form>
    </div>
  );
};

export default UpdateItinerary;
